import React, { Component } from 'react';
import Card from './Card.js'
import './ImageGrid.css';
import listReactFiles from 'list-react-files'

class ImageGrid extends Component {

	constructor(){
    super();
    this.state = {
      image_names: []
    }
  }

	componentDidMount(){
		//I had trouble finding a way to access the file names of a directory
		//Because of that, I decided to just hardcode it for now
		this.setState({image_names: [
      '1.jpg',
      '2.jpg',
      '3.jpg',
      '4.jpg',
      '5.jpg',
      '6.jpg',
      '7.jpg',
      '8.jpg',
      '9.jpg',
      '10.jpg',
      '11.jpg',
      '12.jpg',
      '13.jpg',
      '14.jpg',
      '15.jpg',
      '16.jpg',
      '17.jpg',
      '18.jpg',
      '19.jpg',
      '20.jpg',
      '21.jpg',
      '22.jpg',
      '23.jpg',
      '24.jpg',
      '25.jpg'
    ]});
	}

  render() {
  	console.log(this.state.image_names);
    return (
      <div>
        <div className="container">
        	<div className="row">
			    	<div className="col">
							<Card image_name='/Images/1.jpg'/>
						</div>

						<div className="col">
							<Card image_name='/Images/2.jpg'/>
						</div>

						<div className="col">
							<Card image_name='/Images/3.jpg'/>
						</div>

						<div className="col">
							<Card image_name='/Images/4.jpg'/>
						</div>

						<div className="col">
							<Card image_name='/Images/5.jpg'/>
						</div>
        	</div>
        </div>
      </div>
    );
  }
}

export default ImageGrid;
