import React, { Component } from 'react';
import Card from './Card.js'
import './ImageGrid.css';

class ImageGrid extends Component {

	constructor(){
    super();
    this.state = {
      image_names: [
      	'1.jpg',
	      '2.jpg',
	      '3.jpg',
	      '4.jpg',
	      '5.jpg',
	      '6.jpg',
	      '7.jpg',
	      '8.jpg',
	      '9.jpg',
	      '10.jpg',
	      '11.jpg',
	      '12.jpg',
	      '13.jpg',
	      '14.jpg',
	      '15.jpg',
	      '16.jpg',
	      '17.jpg',
	      '18.jpg',
	      '19.jpg',
	      '20.jpg',
	      '21.jpg',
	      '22.jpg',
	      '23.jpg',
	      '24.jpg',
	      '25.jpg'
      ],
      rows_of_images:[]
    }
  }

	componentDidMount(){
		let list = this.state.image_names;
		let rows_of_images = [];
		let images = [];
		for(var i = 0, size = list.length; i < size ; i++){
			var item = list[i];
			if(i!==0 && i%5===0){
				rows_of_images.push(images);
				images = [];
				images.push(item);
			}
			else{
				images.push(item);
			}
		}
		rows_of_images.push(images);

		this.setState({rows_of_images}, () => {
			console.log(this.state.rows_of_images)
		});

	}

  render() {
		let rows = this.state.rows_of_images.map(row_of_image =>{
			let row = row_of_image.map(image =>{
				let file_name = '/Images/'+image;
				return <div className="col"> <Card image_name={file_name}/> </div>
			});

			return <div className="row"> {row} </div>
		});	

    return (
      <div>
        <div className="container">
        	{rows}
        </div>
      </div>
    );
  }
}

export default ImageGrid;
